// Compiler result to JSON file
import { createComponent } from 'js/declViewModelService';
import { registerDynImportEntry } from 'js/moduleLoader';
import AwParseService from 'js/awParseService';
import AwFilterService from 'js/awFilterService';
import { getField } from 'js/utils';
import { Fragment } from 'react';
let filterSer = AwFilterService.instance;



/*
render function
*/
import { awSourceEditorRenderFunction } from 'js/AwSourceEditorService';


/*
ctx imports
*/

const ctxImportObj = {  };


/*
dynamic import
 */
registerDynImportEntry('js/AwSourceEditorService', () => import('js/AwSourceEditorService'));;

/*
view model
 */
const declViewModelJson = {
    "schemaVersion": "1.0.0",
    "actions": {
        "render": {
            "method": "awSourceEditorRenderFunction",
            "deps": "js/AwSourceEditorService"
        },
        "onContentChange": {
            "actionType": "JSFunction",
            "method": "srcEditorContentChange",
            "inputData": {
                "editorRef": "{{data.monacoEditorInstance}}"
            },
            "deps": "js/AwSourceEditorService"
        }
    },
    "props": {
        "name": {
            "type": "string"
        },
        "content": {
            "type": "field"
        },
        "config": {
            "type": "object"
        }
    },
    "data": {
        "monacoEditorInstance": ""
    },
    "conditions": {},
    "onEvent": [],
    "lifecycleHooks": {
        "render": "render",
        "onUpdate": {
            "action": "onContentChange",
            "observers": [
                "props.value"
            ]
        }
    },
    "i18n": {},
    "_viewModelId": "AwSourceEditor",
    "_uniqueViewModelId": "AwSourceEditor",
    "ctx": {}
};




/*
define component
*/
const Component = createComponent( declViewModelJson, awSourceEditorRenderFunction, undefined, ctxImportObj );

export default Component;
