import AwIconButton from 'viewmodel/AwIconButtonViewModel';

/**
 * render function for AwBackButton
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awBackButtonRenderFunction = ( props ) => {
    const { prePanelId, action, title, fields, i18n } = props;
    const { backCommand } = fields;
    backCommand.fielddata.destPanelId = prePanelId ? prePanelId : null;
    backCommand.fielddata.action = action ? action : '';
    backCommand.fielddata.tooltip = i18n.BACK_BUTTON_TITLE;
    return (
        <div className='sw-row sw-backButton'>
            <AwIconButton command={fields.backCommand} action={action}></AwIconButton>
            <div className='sw-sectionTitle sw-sectionTitleContainer w-12'>{title}</div>
        </div>
    );
};
