// Copyright (c) 2021 Siemens 
import defaultLocationService from 'js/defaultLocationService';
import appCtxService from 'js/appCtxService';
import pageService from 'js/page.service';
import keyboardService from 'js/keyboardService';
import awHeaderService from 'js/awHeaderService';

export const intializeCommonLocation = async() => {
    const normalizedStateName = defaultLocationService.normalizeStateName();
    const locationContext = appCtxService.getCtx( 'locationContext' );
    appCtxService.updateCtx( 'locationContext', {
        'ActiveWorkspace:Location': normalizedStateName,
        'ActiveWorkspace:SubLocation': locationContext && locationContext['ActiveWorkspace:SubLocation']
    } );
    pageService.registerSubLocationContext();
    keyboardService.registerKeyDownEvent();
    const titles = await awHeaderService.getTitles();
    appCtxService.updateCtx( 'location.titles', titles );
    const availablePages = await pageService.getAvailableSubpages( null );
    const tabsInfo = await awHeaderService.constructTabsAndGetActiveTab( availablePages );
    return { subLocationTabCond: defaultLocationService.updateTabs( null ),
        ...titles,
        ...tabsInfo };
};

export default {
    intializeCommonLocation
};
