// Copyright (c) 2020 Siemens
/* eslint-env es6 */

/**
 * Location Listener Trigger for monitor
 *
 * @module js/splmStatsLocationListener
 */
import locationService from 'js/splmStatsLocationService';

/**
 * Location Listener
 */
function SPLMStatsLocationListener() {
    let self = this;

    let _watcher = null;

    self.start = function( _monitor ) {
        _watcher = {
            start: () => {
                _monitor.setTitle( window.location.hash );
                _monitor.run();
            },
            done: () => null
        };
        locationService.addProc( _watcher );
    };

    self.stop = function() {
        locationService.removeProc( _watcher );
    };

    return self;
}

export default SPLMStatsLocationListener;
