// Copyright (c) 2021 Siemens
/* eslint-disable react/display-name */
/* eslint-disable react-hooks/rules-of-hooks */

/**
 * @module js/awEnableWhenHOC
 */
import React from 'react';
import _ from 'lodash';

const displayName = 'AwEnableWhen';

const awEnableWhenHOC = ( Component ) => {
    // https://reactjs.org/docs/forwarding-refs.html#forwarding-refs-to-dom-components
    return React.forwardRef( ( { children, ...props }, ref ) => {
        let { enableWhen, ...finalProps } = props;
        !ref && ( ref = React.useRef( null ) );
        if( typeof Component === 'object' ) {
            Component.displayName = displayName;
        }

        if( enableWhen ) {
            return <Component {...finalProps}>{children}</Component>;
        }
        if( ref && ref.current ) {
            ref.current.onclick = ( e ) => {
                if( e.currentTarget.classList.contains( 'disabled' ) ) {
                    e.stopPropagation();
                }
            };
        }
        if( !_.isEmpty( finalProps.extTooltipData ) ) {
            finalProps.className = finalProps.className ? finalProps.className + ' disabled aw-enableWhen-tooltip' : 'disabled aw-enableWhen-tooltip';
        } else {
            finalProps.className = finalProps.className ? finalProps.className + ' disabled' : 'disabled';
        }
        finalProps.disabled = true;
        return (
            <Component  ref={ref}  {...finalProps}>{children}</Component>
        );
    } );
};

export default awEnableWhenHOC;
