export const updateTheme = ( theme ) => {
    //All the themes available today
    var themes = [ 'ui-lightTheme', 'ui-darkTheme', 'hostedLightTheme' ];

    const updateBodyClass = () => {
        document.body.classList.remove( ...themes );
        document.body.classList.add( theme );
    };

    switch( theme ) {
        case 'ui-lightTheme':
            updateBodyClass();
            break;
        case 'ui-darkTheme':
            import( '../../../css/sw-darkTheme.scss' ).then( ( ) => {
                updateBodyClass();
            } );
            break;
        case 'hostedLightTheme':
            import( '../../../css/hostedLightTheme.scss' ).then( ( ) => {
                updateBodyClass();
            } );
            break;
    }
};

export const awThemeSwitchRenderFunction = ( ) => {
