import { includeImage } from 'js/moduleLoader';

/**
 * render function for AwList
 * @param {*} prop prop
 * @returns {JSX.Element} react component
 */
export const awVisualIndicatorRenderFunction = ( props ) => {
    let { vmo } = props;

    if( !vmo || !vmo.indicators ) {
        return;
    }

    const renderIndicator = ( indicator, index ) => {
        return (
            <div key={index} className='sw-visual-indicator' title={indicator.tooltip}>
                <span>{includeImage( indicator.image )}</span>
            </div>
        );
    };

    return (
        <div className='aw-layout-flexRowContainer' >
            {
                vmo.indicators.map( ( indicator, index ) =>
                    renderIndicator( indicator, index ) )
            }
        </div>
    );
};
