import _ from 'lodash';

export const awExtTooltipRenderFunction = ( { prop, viewModel } ) => {
    const { data } = viewModel;
    let { description, tooltip } = data.command;

    // command tooltip maybe object
    tooltip = _.isObject( tooltip ) ? tooltip.value : tooltip;

    const getContent = () => {
        let result = <div className='font-bold'>{tooltip}</div>;
        if( description ) {
            result = <>
                <div className='aw-extended-tooltipTitle'>{tooltip}</div>
                <div className='new-line'>
                    <br/> { description }
                </div>
            </>;
        }
        return result;
    };

    return getContent();
};
