        element,
        options,
        handle: options.handle && isElement( options.handle ) ? options.handle : element,
        handlers: {
            start: {
                mousedown: start,
                touchstart: start
            },
            move: {
                mousemove: drag,
                mouseup: stop,
                touchmove: drag,
                touchend: stop
