// Compiler result to JSON file
import { createComponent } from 'js/declViewModelService';
import { registerDynImportEntry } from 'js/moduleLoader';
import AwParseService from 'js/awParseService';
import AwFilterService from 'js/awFilterService';
import { getField } from 'js/utils';
import { Fragment } from 'react';
let filterSer = AwFilterService.instance;



/*
render function
*/
import { awGlobalAvatarRenderFunction } from 'js/AwGlobalAvatarService';


/*
ctx imports
*/

const ctxImportObj = {  };


/*
dynamic import
 */
registerDynImportEntry('js/AwGlobalAvatarService', () => import('js/AwGlobalAvatarService'));
registerDynImportEntry('js/locationNavigation.service', () => import('js/locationNavigation.service'));;

/*
view model
 */
const declViewModelJson = {
    "schemaVersion": "1.0.0",
    "data": {
        "userName": {
            "displayName": "{{ctx.userSession.props.user.uiValues[0]}}",
            "type": "STRING"
        },
        "dockUnDockUserSettingsAnchor": {
            "dockUserSettingsLabel": {
                "displayName": "{{i18n.dockUserProperties}}",
                "type": "STRING"
            },
            "undockUserSettingsWhileLHNUnPinnedIconButton": {
                "action": "toggleUserSettingsBarAndCloseLHN",
                "iconName": "uiUndock",
                "tooltip": "{{i18n.undockUserProperties}}"
            },
            "undockUserSettingsWhileLHNPinnedIconButton": {
                "action": "toggleShowUserSettingsBarValue",
                "iconName": "uiUndock",
                "tooltip": "{{i18n.undockUserProperties}}"
            },
            "dockUserSettingsIconButton": {
                "action": "toggleShowUserSettingsBarValue",
                "iconName": "uiDock",
                "tooltip": "{{i18n.dockUserProperties}}"
            }
        }
    },
    "actions": {
        "render": {
            "method": "awGlobalAvatarRenderFunction",
            "deps": "js/AwGlobalAvatarService"
        },
        "viewProfile": {
            "actionType": "JSFunction",
            "method": "go",
            "inputData": {
                "transitionTo": "com_siemens_splm_clientfx_tcui_xrt_showObject",
                "toParams": {
                    "uid": "{{ctx.user.uid}}"
                }
            },
            "deps": "js/locationNavigation.service",
            "events": {
                "success": [
                    {
                        "name": "awPopupWidget.close"
                    }
                ]
            }
        },
        "toggleUserSettingsBarAndCloseLHN": {
            "actionType": "batchJob",
            "steps": [
                {
                    "action": "toggleShowUserSettingsBarValue"
                },
                {
                    "action": "closeLHNGlobalNavigationSideNav"
                }
            ]
        },
        "toggleShowUserSettingsBarValue": {
            "actionType": "modifyCtx",
            "inputData": {
                "methodType": "update",
                "name": "showUserSettingsBar",
                "value": "{{!(ctx.showUserSettingsBar=='true')}}"
            }
        },
        "closeLHNGlobalNavigationSideNav": {
            "actionType": "Event",
            "method": "Event",
            "inputData": {
                "events": [
                    {
                        "name": "awsidenav.openClose",
                        "eventData": {
                            "id": "globalNavigationSideNav",
                            "commandId": "avatarCommandId",
                            "keepOthersOpen": true
                        }
                    }
                ]
            }
        }
    },
    "onEvent": [],
    "i18n": {
        "undockUserProperties": [
            "UIElementsMessages"
        ],
        "dockUserProperties": [
            "UIElementsMessages"
        ]
    },
    "messages": {},
    "conditions": {},
    "ctx": {
        "user.hasThumbnail": null,
        "user.typeIconURL": null,
        "user.thumbnailURL": null,
        "user.cellHeader1": null,
        "tcSessionData": null
    },
    "lifecycleHooks": {
        "render": "render"
    },
    "dataProviders": {
        "userShowCaseLink": {
            "response": [
                {
                    "propDisplayValue": "Profile",
                    "propInternalValue": "profile"
                },
                {
                    "propDisplayValue": "SignOut",
                    "propInternalValue": "SignOut"
                }
            ],
            "totalFound": 2
        },
        "availableWorkspace": {
            "response": [
                {
                    "propDisplayValue": "Category Showcase",
                    "propInternalValue": "Category Showcase"
                },
                {
                    "propDisplayValue": "Item Showcase",
                    "propInternalValue": "Item Showcase"
                }
            ],
            "totalFound": 2
        },
        "projectShowCaseLink": {
            "response": [
                {
                    "propDisplayValue": "Project1",
                    "propInternalValue": "project1"
                },
                {
                    "propDisplayValue": "Project2",
                    "propInternalValue": "project2"
                }
