        "userWorkspaceShowCaseLink": {
            "response": [
                {
                    "propDisplayValue": "Admin Workspace",
                    "propInternalValue": "workspace1"
                },
                {
                    "propDisplayValue": "Context Workspace",
                    "propInternalValue": "workspace2"
                }
            ],
            "totalFound": 2
        }
