    let subPanelContext = props.subPanelContext ;
    let { viewModel: { data, dataProviders, dispatch, conditions, selectionModels, editHandlers, chartProviders, ports  }, grids, ctx, actions, fields, messages, i18n, formProp, viewPath  } = props;
    data = {...data, dataProviders};
    if( subPanelContext && subPanelContext.fields ){
        fields = { ...fields, ...subPanelContext.fields };
    }
    let $index = null;
    return (
            <div className="sw-container">
        <CommonHeaderExistWhen subPanelContext={AwParseService.instance( "props.viewModel" )( { props, data, fields, dataProviders, ctx, i18n, actions, subPanelContext, viewPath, conditions, formProp, dispatch, selectionModels, editHandlers, chartProviders, ports } )} existWhen={AwParseService.instance( "!conditions.isTaskbarFullScreen" )( { props, data, fields, dataProviders, ctx, i18n, actions, subPanelContext, viewPath, conditions, formProp, dispatch, selectionModels, editHandlers, chartProviders, ports } )}>
