        </AwLayoutSlot>
        <DivExistWhen className="afx-medium-background sw-sublocation-tabs" existWhen={AwParseService.instance( "conditions.showSublocations" )( { props, data, fields, dataProviders, ctx, i18n, actions, subPanelContext, viewPath, conditions, formProp, dispatch, selectionModels, editHandlers, chartProviders, ports } )}>
            <AwTabSet  updateHook={AwParseService.instance( "{data, conditions,dispatch }" )( { props, data, fields, dataProviders, ctx, i18n, actions, subPanelContext, viewPath, conditions, formProp, dispatch, selectionModels, editHandlers, chartProviders, ports } )} action={actions.switchSubLocation} tabs={AwParseService.instance( "data.subLocationTabs" )( { props, data, fields, dataProviders, ctx, i18n, actions, subPanelContext, viewPath, conditions, formProp, dispatch, selectionModels, editHandlers, chartProviders, ports } )}>
            </AwTabSet>
