// Copyright (c) 2021 Siemens
/* eslint-disable react/display-name */

/**
 * @module js/awInitHOC
 */
import React from 'react';
import AwParseService from 'js/awParseService';

const displayName = 'AwInit';

const awInitHOC = ( Component ) => {
    // https://reactjs.org/docs/forwarding-refs.html#forwarding-refs-to-dom-components
    return React.forwardRef( ( { children, ...props }, ref ) => {
        let { awInit, parentVM, subPanelContext } = props;
        let intExpr = awInit;
        if( intExpr && parentVM ) {
            const context = { data: parentVM.data, subPanelContext };
            if( intExpr.indexOf( ';' ) !== -1 ) {
                intExpr.split( ';' ).map( ( expr ) => {
                    AwParseService.instance( expr )( context );
                } );
            } else {
                AwParseService.instance( intExpr )( context );
            }
            parentVM.dispatch( 'data', { ...parentVM.data } );
        }
        let finalProps = { ...props };
        ref && ( finalProps.ref = ref );
        if( typeof Component === 'object' ) {
            Component.displayName = displayName;
        }
        return <Component {...finalProps}>{children}</Component>;
    } );
};

export default awInitHOC;
