import awIconSvc from 'js/awIconService';
import AwPic from 'viewmodel/AwPicViewModel';
import AwIcon from 'viewmodel/AwIconViewModel';
import _ from 'lodash';
import { getImageAliasFromId } from 'js/imageRegistry';
export const awTileIconRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;

    let localProps = { ...prop };
    let REGEX_THUMBNAIL_TICKET = /\/|\\|%2f|%5c/ig;
    let imgClass = [ 'aw-base-icon' ];
    let icon;

    if( prop.icon ) {
        if( prop.primary && prop.primary === 'true' ) {
            icon = prop.icon.primaryIcon;
        } else {
            icon = prop.icon.secondaryIcon;
        }
        if( !_.isEmpty( icon ) ) {
            if( REGEX_THUMBNAIL_TICKET.test( icon ) ) {
                localProps.isThumbnail = true;
                imgClass.push( 'aw-tile-thumbnailIcon' );
                localProps.tileIcon = awIconSvc.buildThumbnailFileUrlFromTicket( icon );
            } else if( icon === '__TYPEICON__' && _.isArray( prop.icon.typeHierarchy ) ) {
                localProps.isTypeIcon = true;
                localProps.tileIcon = awIconSvc.getTypeIconFileUrlForTypeHierarchy( prop.icon.typeHierarchy );
            } else {
                // Get the icon contents from the icon service
                let alias = getImageAliasFromId( icon );
                let iconId = icon;
                if( !alias ) {
                    alias = getImageAliasFromId( `home${icon}` );
                    if( alias ) {
                        iconId = `home${icon}`;
                    }
                }
                localProps.tileIcon = iconId;
            }
        }
    }

    return (
        <div className='aw-tile-icon'>
            <div className='aw-tile-iconContainer aw-layout-flexColumn sw-justify-center'>
                {
                    localProps.tileIcon && !localProps.isThumbnail && !localProps.isTypeIcon &&
                <AwIcon iconId={localProps.tileIcon}></AwIcon>
                }
                {
                    ( localProps.isThumbnail || localProps.isTypeIcon ) &&
                <AwPic className={imgClass.join( ' ' )} source={localProps.tileIcon}></AwPic>
                }
            </div>
        </div>
    );
};
