// Copyright (c) 2021 Siemens
/* eslint-disable react-hooks/rules-of-hooks */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable react/display-name */

/**
 * @module js/awRightClickHOC
 */
import React from 'react';

const displayName = 'AwRightClick';

const awClickHOC = ( Component ) => {
    // https://reactjs.org/docs/forwarding-refs.html#forwarding-refs-to-dom-components
    return React.forwardRef( ( { children, ...props }, ref ) => {
        !ref && ( ref = React.useRef( null ) );
        const refCallback = React.useCallback( ( node ) => {
            if( node ) {
                ref.current =  node;
                ref.current.oncontextmenu = ( e ) => handleRightClick( props.awRightClick, e );
            }
        }, [ ref, props.awRightClick ] );
        let finalProps = { ...props };
        const handleRightClick = ( awRightClick, e ) => {
            e.preventDefault();
            awRightClick();
        };
        if( typeof Component === 'object' ) {
            Component.displayName = displayName;
        }
        return (
            // eslint-disable-next-line jsx-a11y/no-static-element-interactions
            <Component {...finalProps} ref={refCallback} domRef={refCallback}>{children}</Component>
        );
    } );
};

export default awClickHOC;
