/**
 * render function for AwPropertyError
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awPropertyErrorRenderFunction = ( props ) => {
    const { children, required } = props;
    if( !children ) {
        return;
    }

    let { props: childProps } = children;
    let errMsg;

    let errorClass = [];
    errorClass.push( required ? 'sw-required' : '' );

    if( childProps.error ) {
        errMsg = childProps.error[ 0 ];
        errorClass.push( 'sw-errorFlag' );
    }

    return (
        <div className={errorClass.join( ' ' )}>
            {
                errMsg &&
                    <span className='sw-property-name' role= 'alert' title= {errMsg} >{ errMsg }</span>
            }
            { children }
        </div>
    );
};
