    httpGetPath = 'darsi/static/config/' + basePath + '.json';
    assetsPath = httpGetPath;
  } else if (_darsiEnabled && /^viewmodel$/.test(basePath)) {
    var key = path.split('.')[1];
    httpGetPath = 'darsi/views/' + key + '/model';
    assetsPath = httpGetPath;
