  } else if (basePath === 'states' || basePath === 'authenticator' || basePath === 'analytics' || basePath === 'OOTB_Verification' || basePath === 'predictiveui' || basePath === 'contributions' || basePath === 'typeProperties' || basePath === 'decorators' || basePath === 'actionTemplateDefs' || basePath === 'aliasRegistry' || basePath === 'solutionDef' || basePath === 'lovDataProvider' || basePath === 'typeFiles' || basePath === 'versionConstants' || basePath === 'clipboard' || basePath === 'workspace' || basePath === 'propStrategy' || basePath === 'highlighter' || basePath === 'nodeTemplates' || basePath === 'filePickerConfiguration') {
