export const getCfgCached = ( path ) => {
    var ndx = path.indexOf( '.' );
    var basePath = ndx > -1 ? path.substring( 0, ndx ) : path;
    if( _.has( exports.config, basePath ) ) {
        return awConfiguration.get( path );
    }
    return getJSConfig( path, basePath );
};

