// Compiler result to JSON file
import { createComponent } from 'js/declViewModelService';
import { registerDynImportEntry } from 'js/moduleLoader';
import AwParseService from 'js/awParseService';
import AwFilterService from 'js/awFilterService';
import { getField } from 'js/utils';
import { Fragment } from 'react';
let filterSer = AwFilterService.instance;
import AwRow from 'viewmodel/AwRowViewModel';
import AwColumn from 'viewmodel/AwColumnViewModel';
import AwLabel from 'viewmodel/AwLabelViewModel';




/*
ctx imports
*/

const ctxImportObj = {  };




/*
view model
 */
const declViewModelJson = {
    "schemaVersion": "1.0.0",
    "data": {
        "helloText": {
            "type": "STRING",
            "uiValue": "{{i18n.helloText}}"
        }
    },
    "i18n": {
        "helloText": [
            "helloModuleMessages"
        ]
    },
    "_viewModelId": "hello",
    "_uniqueViewModelId": "hello",
    "ctx": {}
};

/*
<aw-row height="fill" justify="center" offset="4">
    <aw-column width="fill" justify="center" offset="4">
       <aw-label prop="data.helloText"></aw-label>
    </aw-column>
</aw-row>

*/
const renderFn =(props)=> {
    let subPanelContext = props.subPanelContext ;
    let { viewModel: { data, dataProviders, dispatch, conditions, selectionModels, editHandlers, chartProviders, ports  }, grids, ctx, actions, fields, messages, i18n, formProp, viewPath  } = props;
    data = {...data, dataProviders};
    if( subPanelContext && subPanelContext.fields ){
        fields = { ...fields, ...subPanelContext.fields };
    }
    let $index = null;
    return (
            <AwRow height="fill" justify="center" offset="4">
        <AwColumn width="fill" justify="center" offset="4">
            <AwLabel {...getField("data.helloText", fields, $index, null, null )}>
            </AwLabel>
        </AwColumn>
    </AwRow>
    )
};


/*
define component
*/
const Component = createComponent( declViewModelJson, renderFn, undefined, ctxImportObj );

export default Component;
