import AwWidget from 'viewmodel/AwWidgetViewModel';

/**
 * render function for AwLabel
 * due to historical naming, aw-label is a label and a value...
 * @returns {JSX.Element} react component
 */
export const awLabelRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;
    let value = prop.fielddata.uiValue;

    let fielddata = { ...prop.fielddata };
    fielddata.isEditable = false;
    let disabled = true;
    if( fielddata.labelPlacement !== 'none' ) {
        fielddata.labelPlacement = 'start';
    }

    const newProps = { ...prop, value, disabled, fielddata };

    return (
        <AwWidget {...newProps} ></AwWidget>
    );
};
