// Copyright (c) 2022 Siemens

/**
 * @module js/fullScreenService
 */

export let toggleFullScreenEnablement = function( fullScreenState ) {
    let value = fullScreenState.value;
    fullScreenState.update( !value );
};

export let populateFullScreenHeaderCaption = ( headerTitle, headerPath, headerContextTitle ) => {
    let caption = '';
    if( headerTitle ) {
        caption = headerTitle;
    }
    if( headerPath ) {
        caption = caption ? caption + ' | ' + headerPath : headerPath;
    }
    if( headerContextTitle ) {
        caption = caption ? caption + ' | ' + headerContextTitle : headerContextTitle;
    }
    return caption;
};

const exports = {
    toggleFullScreenEnablement,
    populateFullScreenHeaderCaption
};

export default exports;
