// Copyright (c) 2021 Siemens
import AwIcon from 'viewmodel/AwIconViewModel';

const defaultOptions = { showCellText: true };

export const awLinkPopupMenuItemRenderFunction = ( props ) => {
    const {
        item,
        text,
        options
    } = props;

    const opt = options || defaultOptions;
    return (
        <div data-locator='aw-popup-cellContentContainer' className='aw-popup-cellContentContainer'>
            {opt.showCellIcon && item.iconName && <AwIcon iconId={item.iconName}
                className={`aw-base-icon aw-popup-cellIcon ${!opt.showCellText ? 'aw-cellIconOnly' : ''}`}>
            </AwIcon>}
            { opt.showCellText && <span>{text( item )}</span> }
        </div>

    );
};
