// Copyright (c) 2021 Siemens
import wcagService from 'js/wcagService';
import _ from 'lodash';

export const initializeStandardSublocation = ( provider ) => {
    const sublocationStyles = {};
    if( provider.awSubLocationPref ) {
        const verticalLayout = provider.awSubLocationPref.orientation === 'HORIZONTAL';
        const flipLayout = provider.awSubLocationPref.flipLayout;

        if( verticalLayout ) {
            if( flipLayout ) {
                sublocationStyles.verticalReverse = true;
            } else {
                sublocationStyles.vertical = true;
            }
        } else {
            if( flipLayout ) {
                sublocationStyles.horizontalReverse = true;
            }
        }
    }
    wcagService.applyFocusOnMain();
    wcagService.updateArialabelForDuplicateLandmarks();
    return sublocationStyles;
};

export const updateSearchStateWithSelection = ( searchState, selectionData ) => {
    if( !_.isEmpty( selectionData ) ) {
        let newSearchstate = searchState ? { ...searchState.value } : undefined;
        if( newSearchstate ) {
            if( selectionData.source === 'primary' ) {
                newSearchstate.pwaSelection = selectionData.selected ? selectionData.selected : [];
            } else if( selectionData.source === 'base' || selectionData.source === undefined ) {
                newSearchstate.pwaSelection = [];
            }
            searchState.update( newSearchstate );
        }
    }
};
