/**
 * @module js/AwCellCommandBarService
 */
import AwCellCommandList from 'viewmodel/AwCellCommandListViewModel';

export const awCellCommandBarRenderFunction = ( { alignment, anchor, context, viewModel: { data: { commandList = null } } } ) => {
    return commandList ? <AwCellCommandList commandContext={ context } commandList={ commandList } alignment={ alignment } anchor={ anchor }></AwCellCommandList> : null;
};
