/**
 * @module js/AwCellCommandListService
 */
import AwCommand from 'viewmodel/AwCommandViewModel';

export const awCellCommandListRenderFunction = ( { alignment, commandContext, ctxMin: { commandDisplays }, anchor } ) => {
    return <div className='aw-cell-command-bar' anchor={ anchor }>
        { commandDisplays.filter( x => x.visible && ( !x.cellDisplay || x.cellDisplay.position === 'TOP_RIGHT' ) ).map( command =>
            <AwCommand commandContext={commandContext} key={command.id} command={command} alignment={alignment}></AwCommand> )}
        { commandDisplays.filter( x => x.visible && ( x.cellDisplay && x.cellDisplay.position === 'MANUAL_STYLE' ) ).map( command =>
            <AwCommand commandContext={commandContext} key={command.id} command={command} alignment={alignment}></AwCommand> )}
        <div className='aw-layout-flexRow'></div>
        { commandDisplays.filter( x => x.visible && ( x.cellDisplay && x.cellDisplay.position === 'MIDDLE_RIGHT' ) ).map( command =>
            <AwCommand commandContext={commandContext} key={command.id} command={command} alignment={alignment}></AwCommand> )}
        <div className='aw-layout-flexRow'></div>
        { commandDisplays.filter( x => x.visible && ( x.cellDisplay && x.cellDisplay.position === 'BOTTOM_RIGHT' ) ).map( command =>
            <AwCommand commandContext={commandContext} key={command.id} command={command} alignment={alignment}></AwCommand> )}
    </div>;
};

