/**
 * @module js/CommonAppLogoService
 */
import AwAvatar from 'viewmodel/AwAvatarViewModel';

/**
 * render function for Common Right Edge Navigation
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const CommonAppLogoRenderFunction = ( { viewModel } ) => {
    const data = viewModel.data;
    return (
        <div className='sw-component sw-avatar'>
            <AwAvatar size='xsmall' source={data.appLogo}></AwAvatar>
        </div>
    );
};
