/**
 * @module js/CommonGlobalNavigationService
 */
import AwSidenav from 'viewmodel/AwSidenavViewModel';
import AwLayoutSlot from 'viewmodel/AwLayoutSlotViewModel';

/**
 * render function for Common Global Navigation
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const commonGlobalNavigationRenderFunction = ( { viewModel, i18n } ) => {
    const { data } = viewModel;
    return (
        <nav aria-label={i18n.PRIMARY_NAVIGATION} className='sw-row h-12'>
            <div className='sw-column flex-auto global-navigation-toolbar'>
                <AwLayoutSlot name='aw_appLogo' className='aw-layout-fit' context={data}></AwLayoutSlot>
                <AwLayoutSlot name='aw_globalNavigationBar' className='aw-layout-fill' context={data}></AwLayoutSlot>
                <AwLayoutSlot name='aw_avatar' className='aw-layout-fit' context={data}></AwLayoutSlot>
            </div>
            <div className='sw-column flex-auto global-navigation'>
                <AwSidenav id='globalNavigationSideNav' config={data.sideNavData}></AwSidenav>
            </div>
        </nav>
    );
};
