/**
 * @module js/CommonUserSettingsBarService
 */
import AwAdvancedBar from 'viewmodel/AwAdvancedBarViewModel';
import AwFlexRow from 'viewmodel/AwFlexRowViewModel';
import AwIconButton from 'viewmodel/AwIconButtonViewModel';
import AwHeaderContext from 'viewmodel/AwHeaderContextViewModel';

export const commonUserSettingsRenderFunction = ( { fields, viewModel, ctx } ) => {
    const { conditions } = viewModel;
    const settingsClass = conditions.isUserSettingsBarVisible ? 'aw-roll-out' : 'aw-roll-in';
    return (
        <footer>
            {conditions.isUserSettingsBarVisible ? <div className={'aw-user-settings-bar ' + settingsClass}>
                <AwAdvancedBar type='FOOTER'>
                    <AwFlexRow justify='center' align-content='center'>
                        {ctx.showUserSettingsBar === 'true' ? <AwHeaderContext alignment='HORIZONTAL'></AwHeaderContext> : ''}
                        <div className='sw-userSettings-closeButton'>
                            <AwIconButton command={fields.close}></AwIconButton>
                        </div>
                    </AwFlexRow>
                </AwAdvancedBar></div> : ''}
        </footer>
    );
};
