/**
 * render function for AwBreadCrumb
 * @deprecated afx@4.2.0.
 * @alternative <br>
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */
import AwBreadCrumbItem from 'viewmodel/AwBreadCrumbItemViewModel';

export const awBreadCrumbServiceRenderFn = ( { viewmodel, fields, prop } ) => {
    const { crumbs, bcId, compact } = prop;
    let popuplist = prop.popuplist || 'AwDefaultBreadCrumbList';
    const createCrumbs = ( crumbs ) => {
        return crumbs.map( ( crumb ) => {
            return (
                <div className='sw-column justify-center' key={crumb.displayName}>
                    <AwBreadCrumbItem
                        crumb={crumb}
                        popuplist={popuplist}
                    ></AwBreadCrumbItem>
                </div>
            );
        } );
    };

    let getClassNames = () => {
        let classNames = [];
        classNames.push( 'aw-layout-fxbreadCrumbs' );
        classNames.push( 'aw-layout-flexColumn' );
        if( compact ) {
            classNames.push( 'aw-widgets-breadCrumbCompact' );
        }
        return classNames.join( ' ' );
    };

    return (
        <h1 className={getClassNames()}>
            <div className='sw-row justify-left'>{createCrumbs( crumbs )}</div>
        </h1>
    );
};
