import AwPropertyLabel from 'viewmodel/AwPropertyLabelViewModel';
import AwCheckboxVal from 'viewmodel/AwCheckboxValViewModel';

/**
 * render function for AwCheckbox
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awCheckboxRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;

    prop.fielddata.renderingHint = 'checkbox';

    return (
        <AwPropertyLabel {...prop}>
            <AwCheckboxVal {...prop}></AwCheckboxVal>
        </AwPropertyLabel>
    );
};
