import AwDatetime from 'viewmodel/AwDatetimeViewModel';

export const awDateRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;
    let fielddata = { ...prop.fielddata };

    let dateApi = { ...prop.fielddata.dateApi };
    dateApi.isDateEnabled = true;
    dateApi.isTimeEnabled = false;
    fielddata.dateApi = dateApi;

    let newProps = { ...prop, fielddata };
    return (
        <AwDatetime {...newProps}></AwDatetime>
    );
};
