import AwPropertyLabel from 'viewmodel/AwPropertyLabelViewModel';
import AwDateTimeVal from 'viewmodel/AwDateTimeValViewModel';

export const awDatetimeRenderFunction = ( props ) => {
    let labelName = props.fielddata.propertyDisplayName;

    if( !labelName || labelName === 'NO_PROPERTY_LABEL' || labelName === 'PROPERTY_LABEL_AT_RIGHT' ) {
        return <AwDateTimeVal {...props}></AwDateTimeVal>;
    }
    return (
        <AwPropertyLabel {...props}>
            <AwDateTimeVal {...props}></AwDateTimeVal>
        </AwPropertyLabel>
    );
};
