import _ from 'lodash';
import AwButton from 'viewmodel/AwButtonViewModel';

export const awFileUploadLabelFunction = ( props ) => {
    const { name, size } = props.item;
    let displayedSize = size ? `(${ ( size / 1000000 ).toFixed( 3 )}${props.i18n.MB})` : null;
    let { removeTooltip } = props;
    removeTooltip = removeTooltip === undefined ? props.i18n.removeFileBtnTooltip : removeTooltip;
    return (
        <div className='sw-row aw-file-upload-details'>
            <div className = 'aw-file-upload-text'>{name}</div>
            <div className = 'propertyLabelTopContainer' >
                { displayedSize && <div className = 'aw-file-upload-text aw-normal-size'>{displayedSize}</div> }
                <AwButton iconId = 'cmdClearText' tooltip={removeTooltip} action = { ( e ) => props.removeFileHandler( e, name ) }> </AwButton>
            </div>
        </div>
    );
};
