import flexService from 'js/awFlexService';

/**
 * render function for AwFlexColumn
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awFlexColumnRenderFunction = ( props ) => {
    var config = {
        offset: props.offset,
        offsetRight: props.offsetRight
    };

    let stylingResponse = flexService.getStyles( config, props, {}, 'width' );

    return (
        <div className={ 'sw-flex-column' + ' ' + stylingResponse.classes } style={stylingResponse.styles}>
            {props.children}
        </div>
    );
};
