import flexService from 'js/awFlexService';

/**
 * render function for AwFlexRow
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awFlexRowRenderFunction = ( props ) => {
    var config = {
        offset: props.offset,
        offsetBottom: props.offsetBottom
    };

    let stylingResponse = flexService.getStyles( config, props, {}, 'height' );

    return (
        <div className={ 'sw-flex-row' + ' ' + stylingResponse.classes } style={stylingResponse.styles}>
            {props.children}
        </div>
    );
};
