/**
 * render function for AwFrame
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awFrameRenderFunction = ( props ) => {
    var config = {
        url: props.url,
        frameTitle: props.frameTitle && props.frameTitle !== '' ? props.frameTitle : 'iframe_' + Math.random( 1000 ),
        frameHeight: props.height || '100',
        frameWidth: props.width || '100'
    };

    return (
        <iframe className='aw-jswidgets-htmlPanelFrame'
            src={config.url}
            height={config.frameHeight}
            width={config.frameWidth}
            title={config.frameTitle}
            allowFullScreen></iframe>

    );
};
