import AwWidget from 'viewmodel/AwWidgetViewModel';

/**
 * render function for AwHeaderProps
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awHeaderPropsRenderFunction = ( props ) => {
    const properties = props.headerproperties || props.headerProperties;
    if( properties && properties.length ) {
        let headerProps = [];
        for( const headerProp of properties ) {
            let headerPropOverflowCls = headerProp.renderingHint === 'overflow' ? ' aw-layout-headerPropertyOverflow' : '';
            headerProps.push(
                <AwWidget key={headerProp.property.value} {...headerProp.property} className={'aw-layout-headerProperty' + headerPropOverflowCls} renderingHint={headerProp.renderingHint} modifiable={false} ></AwWidget> );
        }
        return <div data-locator='aw-header' className='sw-row aw-layout-headerProperties'>
            {headerProps}
        </div>;
    }
};
