import AwModelIcon from 'viewmodel/AwModelIconViewModel';
import AwVisualIndicator from 'viewmodel/AwVisualIndicatorViewModel';
import AwHeaderProps from 'viewmodel/AwHeaderPropsViewModel';

/**
 * render function for AwList
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const AwHeaderTitleRenderFunction = ( props ) => {
    return (
        <div className='aw-layout-headerLeft aw-layout-flexRowContainer aw-layout-locationTitlePanel'>
            <div className='aw-layout-flexColumnContainer'>
                { renderModelIcon( props ) }
            </div>
            <div className='aw-layout-flexColumn'>
                <h1 data-locator='aw-layout-locationTitle' className='aw-layout-locationTitle'>{ props.headertitle }</h1>
                { renderVisualIndicator( props ) }
            </div>
        </div>
    );
};

const renderModelIcon = ( prop ) => {
    if( prop.headervmo && prop.headervmo.props ) {
        return (
            <div data-locator='aw-layout-headerIcon' className='aw-layout-headerIcon'>
                <AwModelIcon vmo={prop.headervmo}></AwModelIcon>
            </div>
        );
    }
};

const renderVisualIndicator = ( prop ) => {
    if( prop.headervmo && prop.headervmo.indicators && prop.headervmo.indicators.length > 0 ) {
        return (
            <div className='aw-layout-headerPropContainer aw-layout-flexRowContainer'>
                {
                    <AwVisualIndicator vmo={prop.headervmo} />
                }
                {
                    <AwHeaderProps headerproperties={prop.headerproperties} />
                }
            </div>
        );
    }
};
