import AwDefaultCell from 'viewmodel/AwDefaultCellViewModel';
import awIconService from 'js/awIconService';
import AwPic from 'viewmodel/AwPicViewModel';

/**
 * render function for AwBackButton
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awImageCellRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;
    const thumbnailUrl = awIconService.getThumbnailFileUrl( prop.vmo );
    return (
        <div className='sw-imagecell-container'>
            {thumbnailUrl ? <div className='sw-imagecell-thumbnailContainer'><AwPic source={thumbnailUrl} className='sw-imagecell-thumbnail'></AwPic></div> : ''}
            <AwDefaultCell {...prop}></AwDefaultCell>
        </div>
    );
};
