import AwPic from 'viewmodel/AwPicViewModel';

/**
 * @deprecated afx@4.2.0.
 * @alternative AwPic
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */
export const awImageRenderFunction = ( props ) => {
    if( props.isIcon ) {
        return <AwPic iconId={props.source} className='sw-image-icon'></AwPic>;
    }
    return <AwPic source={props.source}></AwPic>;
};
