/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable jsx-a11y/no-static-element-interactions */
export const awItemRendererRenderFunction = ( props ) => {
    const { item, options } = props;

    let action = null;
    let key = 'Title';
    if( options ) {
        action = options.action;
        key = options.key;
    }

    return (
        <div className='sw-property-name' onClick={action}>{item[key]}</div>
    );
};
