import AwLink from 'viewmodel/AwLinkViewModel';

/**
 * @deprecated afx@4.2.0.
 * @alternative AwLink
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */
export const AwLinkWithPopupAndIconRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;
    const {
        disable,
        dataprovider,
        children
    } = prop;

    const finalProps = {
        ...prop,
        disabled: disable,
        ellipsis: true,
        linkOptions: {
            hasPopup: true,
            content: children,
            dataProvider: dataprovider,
            iconOptions: {
                title: 'both',
                cell: 'both'
            }
        }
    };

    return <AwLink {...finalProps} />;
};
