import AwLink from 'viewmodel/AwLinkViewModel';

/**
 * @deprecated afx@4.2.0.
 * @alternative AwLink
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */
export const AwLinkWithPopupRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;
    const {
        disabled,
        children,
        id,
        linkPopupId,
        useIcon
    } = prop;

    const uid = id || linkPopupId;

    let finalProps = {
        ...prop,
        id: uid,
        ellipsis: true,
        linkOptions: {
            hasPopup: true,
            content: children
        }
    };

    return <AwLink {...finalProps} />;
};
