import AwPropertyLovVal from 'viewmodel/AwPropertyLovValViewModel';
import AwCommandBar from 'viewmodel/AwCommandBarViewModel';

import _ from 'lodash';

/**
 * render function for AwLovEdit
 * @param {*} param0 context for render function interpolation
 * @returns {JSX.Element} react component
 */
export const awLovEditRenderFunction = ( props ) => {
    const { i18n, viewModel, anchor, ...prop } = props;

    return (
        <div className='aw-layout-flexRowContainer' >
            <AwPropertyLovVal {...prop} ></AwPropertyLovVal>
            {anchor && <AwCommandBar className='aw-popup-pinMargin' anchor={anchor} context={prop} alignment='HORIZONTAL'/>}
        </div>
    );
};
