const renderImage = ( vmo, hideoverlay ) => {
    if( !vmo ) {
        return;
    }
    if( vmo.hasThumbnail ) {
        if( !hideoverlay ) {
            return (
                <div className='aw-widgets-modelIconContainer'>
                    <div className='aw-widgets-modelObjectThumbnail'>
                        <img className='aw-base-icon' src={vmo.thumbnailURL} alt={vmo.cellHeader1}/>
                    </div>
                    <div data-locator='aw-widgets-modelObjectTypeIconOverlay' className='aw-widgets-modelObjectTypeIconOverlay'>
                        <img className='aw-base-icon' src={vmo.typeIconURL} alt={vmo.cellHeader1}/>
                    </div>
                </div>
            );
        }
        return (
            <div className='aw-widgets-modelObjectThumbnail'>
                <img className='aw-base-icon' src={vmo.thumbnailURL} alt={vmo.cellHeader1}/>
            </div>
        );
    }
    if( vmo.typeIconURL ) {
        return (
            <img className='aw-base-icon aw-type-icon' src={vmo.typeIconURL} alt={vmo.modelType && vmo.modelType.displayName ? vmo.modelType.displayName : ''} />
        );
    }
};
export const awModelIconRenderFunction = ( props ) => {
    const { vmo, hideoverlay } = props;
    return renderImage( vmo, hideoverlay );
};
