/**
 * render function for AwBreadCrumb
 * @deprecated afx@4.2.0.
 * @alternative <br>
 * @obsoleteIn afx@5.1.0
 * @returns {JSX.Element} react component
 */
import AwPanelHeader from 'viewmodel/AwPanelHeaderViewModel';
import AwCommandSubPanel from 'viewmodel/AwCommandSubPanelViewModel';
import AwBackButton from 'viewmodel/AwBackButtonViewModel';

export const awNavigatePanelRenderFn = ( props ) => {
    let { prePanelId, destPanelId, panelId, title, className, action } = props;
    const { ...prop } = props;
    if( destPanelId ) {
        panelId = destPanelId;
    }
    return (
        <div className= { className + ' aw-layout-flexColumn'}  >
            <AwPanelHeader className='aw-layout-navigatePanelHeader'>
                <AwBackButton
                    title={title}
                    prePanelId={prePanelId}
                    action={action}>
                </AwBackButton>
            </AwPanelHeader>
            <AwCommandSubPanel
                className='aw-layout-panelBody aw-layout-flexColumn'
                panelId={panelId} {...prop}>
            </AwCommandSubPanel>
        </div>
    );
};
