/* eslint-disable jsx-a11y/label-has-associated-control */
import { ExtendedTooltip } from 'js/hocCollection';
export const awOmdbCellRenderFunction = ( props ) => {
    const { vmo, tconfig } = props; //Assume item is ViewModelObject (VMO)
    const ImgExtendedTooltip = ExtendedTooltip( 'img' );
    if( !vmo ) {
        console.warn( 'AwOmdbCell: invalid vmo!' );
        return undefined;
    }

    return (
        tconfig  ?  <div className = 'aw-default-cell sw-row'>
            {/* Cell list image */}
            <div className='sw-cell-image'>
                <ImgExtendedTooltip className='aw-base-icon' src={vmo.img_src || vmo.Poster} alt={vmo.Title}
                    extendedTooltip={tconfig}
                    extendedTooltipContext={vmo}
                    extendedTooltipOptions={{ alignment : 'RIGHT_CENTER' }}/>
            </div>

            {/* Cell list content */}
            <div className='aw-widgets-cellListCellContent'>
                <div className='aw-widgets-cellListCellTitleBlock'>
                    <h5 title={vmo.Title} className='sw-cell-title'>{vmo.Title}</h5>
                    <label className='aw-widgets-cellListCellItemType' title={vmo.Year}>{vmo.Year}</label>
                </div>
                <div id='cellProperties'>
                    <div className='sw-defaultCell-properties'>
                        <label className='aw-widgets-propertyLabel'
                        >ImdbID:</label>
                        <label className='aw-widgets-propertyValue' title={vmo.id}>{vmo.id}</label>
                    </div>
                </div>
            </div>
        </div>
            : <div className = 'aw-default-cell sw-row'>
                {/* Cell list image */}
                <div className='sw-cell-image'>
                    <img className='aw-base-icon' src={vmo.img_src || vmo.Poster} alt={vmo.Title}/>
                </div>

                {/* Cell list content */}
                <div className='aw-widgets-cellListCellContent'>
                    <div className='aw-widgets-cellListCellTitleBlock'>
                        <h5 title='{vmo.Title}' className='sw-cell-title'>{vmo.Title}</h5>
                        <label className='aw-widgets-cellListCellItemType' title='{vmo.Year}'>{vmo.Year}</label>
                    </div>
                    <div id='cellProperties'>
                        <div className='sw-defaultCell-properties'>
                            <label className='aw-widgets-propertyLabel'
                                // extended-tooltip-context='vmo' extended-tooltip='{tconfig}'
                                // extended-tooltip-options="{alignment : 'RIGHT_CENTER'}"
                            >ImdbID:</label>
                            <label className='aw-widgets-propertyValue' title='{vmo.imdbID}'>{vmo.imdbID}</label>
                        </div>
                    </div>
                </div>
            </div> );
};
