import sanitizer from 'js/sanitizer';
export const awPanelBodyRenderFunction = ( props ) => {
    const {
        viewModel,
        ctxMin,
        actions,
        i18n,
        fields,
        grids,
        formProp,
        messages,
        ...prop
    } = props;
    let { scrollable } = prop;

    // ensure scrollable by default
    if( scrollable === undefined ) { scrollable = true; }

    const getClass = () => {
        return `${!scrollable ? 'disable-scroll' : 'aw-base-scrollPanel'} aw-panelBody flex-auto align-self-stretch sw-column`;
    };
    let sanitizedProps = sanitizer.sanitizeHTMLTagProps( prop, 'div' );
    return (
        <div data-locator={scrollable ? 'aw-panelBody-scrollable' : 'aw-panelBody-nonScrollable'} {...sanitizedProps} className={getClass()}>
            { prop.children }
        </div>
    );
};
